(window.webpackJsonp=window.webpackJsonp||[]).push([[201],{717:function(n,e,t){"use strict";t.r(e),e.default="const {Scene, Sprite, Label} = spritejs;\nconst container = document.getElementById('stage');\nconst scene = new Scene({\n  container,\n  width: 1200,\n  height: 600,\n});\nconst layer = scene.layer();\n\nconst label = new Label('\u8bd5\u8bd5\u5c06\u9f20\u6807\u79fb\u52a8\u5230\u5de6\u53f3\u4e24\u4e2a\u65b9\u5757\u4e0a\uff1a');\nlabel.attr({\n  anchor: 0.5,\n  pos: [400, 50],\n  fontSize: '2rem',\n});\nlayer.append(label);\n\nconst left = new Sprite();\nleft.attr({\n  anchor: 0.5,\n  pos: [300, 300],\n  size: [200, 200],\n  bgcolor: 'red',\n});\nlayer.append(left);\n\nconst right = left.cloneNode();\nright.attr({\n  pos: [700, 300],\n  bgcolor: 'green',\n});\nlayer.append(right);\n\nlet leftTrans = null;\nleft.addEventListener('mouseenter', (evt) => {\n  if(leftTrans) leftTrans.cancel();\n  leftTrans = left.transition(1.0);\n  leftTrans.attr({\n    rotate: 180,\n    bgcolor: 'green',\n  });\n});\nleft.addEventListener('mouseleave', (evt) => {\n  leftTrans.attr({\n    rotate: 0,\n    bgcolor: 'red',\n  });\n});\n\nlet rightTrans = null;\nright.addEventListener('mouseenter', (evt) => {\n  if(rightTrans) rightTrans.cancel();\n  rightTrans = right.transition(3.0);\n  rightTrans.attr({\n    rotate: 720,\n    bgcolor: 'red',\n  });\n});\nright.addEventListener('mouseleave', (evt) => {\n  rightTrans.reverse();\n});"}}]);