(window.webpackJsonp=window.webpackJsonp||[]).push([[245],{761:function(n,e,o){"use strict";o.r(e),e.default="const {Scene, Sprite} = spritejs;\nconst container = document.getElementById('stage');\nconst scene = new Scene({\n  container,\n  width: 1200,\n  height: 1200,\n});\n\nconst layer = scene.layer('fglayer');\n\nfor(let i = 0; i < 10; i++) {\n  for(let j = 0; j < 10; j++) {\n    const s = new Sprite({\n      width: 100,\n      height: 100,\n      x: i * 120,\n      y: j * 120,\n      bgcolor: `hsl(${Math.random() * 360}, 50%, 50%)`,\n    });\n    layer.append(s);\n  }\n}\n\nconst fragment = `precision mediump float;\n  varying vec3 vTextureCoord;\n  uniform sampler2D u_texSampler;\n  uniform vec2 u_mousePos;\n  uniform vec2 u_resolution;\n  void main() {\n    vec2 st = gl_FragCoord.xy / u_resolution;\n    st.y = 1.0 - st.y;\n    float d = distance(st, u_mousePos);\n    vec4 color = texture2D(u_texSampler, vTextureCoord.xy);\n    color.a *= mix(0.1, 1.0, (1.0 - smoothstep(0.0, 0.2, d)));\n    color.rgb *= 1.0 - step(0.2, d);\n    gl_FragColor = color;\n  }\n`;\n\nconst {width, height} = layer.getResolution();\nlayer.addPass({fragment,\n  uniforms: {\n    u_resolution: [width, height],\n    u_mousePos: [0.1, 0.1],\n  }});\n\nlayer.addEventListener('mousemove', (e) => {\n  // const {width, height} = layer.getResolution();\n  const x = e.x / width,\n    y = e.y / height;\n  layer.pass[0].setUniforms({\n    u_mousePos: [x, y],\n  });\n});\n\nlayer.tick();"}}]);